package Forloopprograms;

public class Printatoz {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch;
		for(ch='a'; ch<='z'; ch++)
		{
			System.out.println(ch);
		}
		System.out.println("\n");
		for(ch='A'; ch<='Z'; ch++)
		{
			System.out.println(ch);
		}

	}

}
