package com.ifelse;

public class Divisiblenum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1=15;
		if(n1%2==0 &&n1%3==0)
			System.out.println(n1+ " number is divisible by 2 & 3 ");
		else
			System.out.println(n1+ " number is not  divisible by 2 & 3 ");	
		
			

	}

}
